mapper.select:
select n_no, n_writer, n_title, n_content, n_viewCnt, date_format( n_regDate, '%Y-%m-%d' ) as n_regDate, n_type from t_notice where n_type = #{ n_type }
AND (n_content like CONCAT('%',#{titlecontent},'%') or n_title like CONCAT('%',#{titlecontent},'%'))
order by n_no DESC limit #{startPoint}, #{row}

select n_no, n_writer, n_title, n_content, n_viewCnt, date_format( n_regDate, '%Y-%m-%d' ) as n_regDate, n_type
from t_notice 
where n_no = #{ n_no }

		select t_m_order.mo_no, t_m_order.mo_orderNo, t_m_order.mo_startTime, t_e_order.eo_status, t_e_order.e_no, t_employee.e_name, t_member.m_name, t_member.m_address, t_member.m_lon, t_member.m_lat
        from (((t_m_order inner join (select * from t_e_order where
       	
        	 e_no =#{employeeVo.e_no}       
              
         ) as t_e_order on t_m_order.mo_no = t_e_order.mo_no)
        inner join t_member on t_m_order.m_no = t_member.m_no) inner join t_employee on t_employee.e_no = t_e_order.e_no) 
        where date_format(t_m_order.mo_startTime, '%Y-%m-%d') = date_format(now(),'%Y-%m-%d')  
        order by mo_startTime asc;

mapper.update:
update t_e_order
 set eo_startTime = #{eo_startTime}
 , eo_status = '출동중'  
 , eo_status = '해방중'  
 , eo_status = '해방완료' 
  where mo_no = #{mo_no}

CREATE EVENT ${mo_orderNo} ON SCHEDULE EVERY 40 MINUTE STARTS CURRENT_TIMESTAMP + INTERVAL 40 MINUTE DO BEGIN Insert into t_cancelCall(mo_no,e_no) values(#{mo_no}, (select e_no_first from t_m_order where mo_no=#{mo_no}));
if (Select LastOne.e_no from (Select AA.c_no, AA.e_no, AA.e_name, AA.m_no, AA.m_name, AA.m_lon, AA.m_lat, AA.lonlatGrade, BB.starGrade, ZZZ.profitGrade, (AA.lonlatGrade+BB.starGrade+ZZZ.profitGrade) as totalGrade from ((select SSS.e_no, SSS.c_no, SSS.e_name, B.mo_no, B.m_no, B.mo_startTime, t_member.m_address, t_member.m_name, t_member.m_lon, t_member.m_lat, (case when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.5 then 1 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.4 then 1.5 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.3 then 2 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.2 then 2.5 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.1 then 3 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.05 then 3.5 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >= 0 then 4 else 1 end) as lonlatGrade from ((select t_employee.e_no, t_employee.e_name, t_employee.c_no from (select A.mo_no, t_e_order.e_no from t_e_order inner join (select * from t_m_order where date_format(mo_startTime,"%Y%m%d%H") = #{refuseMoNo.dateHourForNoOneEmployee}) as A on t_e_order.mo_no=A.mo_no) as YY right join t_employee on YY.e_no = t_employee.e_no where YY.e_no is null) as SSS left outer join (select C.mo_no, C.m_no, C.mo_startTime, t_e_order.e_no from t_e_order inner join (select * from t_m_order where date_format(mo_startTime,"%Y%m%d") = #{refuseMoNo.dateForNoOneEmployee} and date_format(mo_startTime,"%Y%m%d%H") != #{refuseMoNo.dateHourForNoOneEmployee} ) as C on t_e_order.mo_no = C.mo_no) as B on SSS.e_no =B.e_no) left join t_member on t_member.m_no = B.m_no) as AA inner join (select t_employee.c_no, if((AVG(D.mo_star)*4/5)>=1,(AVG(D.mo_star)*4/5),1) as starGrade from ((select mo_no, mo_startTime,mo_star from t_m_order where (date_format(mo_startTime, "%Y%m") = if(substring((date_format(now(), "%Y%m")-1),5,2)='00', concat(substring((date_format(now(), "%Y%m")-1),1,4)-1,'12'), date_format(now(), "%Y%m")-1))) as D inner join t_e_order on D.mo_no = t_e_order.mo_no) inner join t_employee on t_employee.e_no = t_e_order.e_no group by t_employee.c_no) as BB on AA.c_no= BB.c_no) inner join (select t_employee.c_no, count(t_service.s_price), sum(t_service.s_price), (case when sum(t_service.s_price)-#{prevMonthOutCome}=0 then 3 when sum(t_service.s_price)-#{prevMonthOutCome} >2000000 then 1.1 when sum(t_service.s_price)-#{prevMonthOutCome} >1600000 then 1.3 when sum(t_service.s_price)-#{prevMonthOutCome} >1400000 then 1.5 when sum(t_service.s_price)-#{prevMonthOutCome} >1200000 then 1.7 when sum(t_service.s_price)-#{prevMonthOutCome} >1000000 then 1.9 when sum(t_service.s_price)-#{prevMonthOutCome} >500000 then 2.1 when sum(t_service.s_price)-#{prevMonthOutCome} >100000 then 2.3 when sum(t_service.s_price)-#{prevMonthOutCome} >50000 then 2.5 when sum(t_service.s_price)-#{prevMonthOutCome} >20000 then 2.7 when sum(t_service.s_price)-#{prevMonthOutCome} >10000 then 2.9 else 2.95 end) as profitGrade from (((t_m_order inner join t_service on t_m_order.s_no=t_service.s_no) inner join t_e_order on t_e_order.mo_no=t_m_order.mo_no) inner join t_employee on t_employee.e_no= t_e_order.e_no) where date_format(t_m_order.mo_startTime, "%Y%m") = if(substring((date_format(now(), "%Y%m")-1),5,2)='00', concat(substring((date_format(now(), "%Y%m")-1),1,4)-1,'12'), date_format(now(), "%Y%m")-1) group by t_employee.c_no) as ZZZ on AA.c_no = ZZZ.c_no) as LastOne left join ((select * from t_cancelCall where mo_no=#{mo_no}) as t_cancelCall) on t_cancelCall.e_no = LastOne.e_no where t_cancelCall.e_no is null order by totalGrade desc, starGrade desc limit 1) is null Then delete from t_cancelCall where mo_no=#{mo_no}; End if;
Update t_m_order set e_no_first=(Select LastOne.e_no from (Select AA.c_no, AA.e_no, AA.e_name, AA.m_no, AA.m_name, AA.m_lon, AA.m_lat, AA.lonlatGrade, BB.starGrade, ZZZ.profitGrade, (AA.lonlatGrade+BB.starGrade+ZZZ.profitGrade) as totalGrade from ((select SSS.e_no, SSS.c_no, SSS.e_name, B.mo_no, B.m_no, B.mo_startTime, t_member.m_address, t_member.m_name, t_member.m_lon, t_member.m_lat, (case when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.5 then 1 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.4 then 1.5 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.3 then 2 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.2 then 2.5 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.1 then 3 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >=0.05 then 3.5 when (ABS(t_member.m_lon -#{refuseMoNo.lon}) + (ABS(t_member.m_lat-#{refuseMoNo.lat}))*3.3) >= 0 then 4 else 1 end) as lonlatGrade from ((select t_employee.e_no, t_employee.e_name, t_employee.c_no from (select A.mo_no, t_e_order.e_no from t_e_order inner join (select * from t_m_order where date_format(mo_startTime,"%Y%m%d%H") = #{refuseMoNo.dateHourForNoOneEmployee}) as A on t_e_order.mo_no=A.mo_no) as YY right join t_employee on YY.e_no = t_employee.e_no where YY.e_no is null) as SSS left outer join (select C.mo_no, C.m_no, C.mo_startTime, t_e_order.e_no from t_e_order inner join (select * from t_m_order where date_format(mo_startTime,"%Y%m%d") = #{refuseMoNo.dateForNoOneEmployee} and date_format(mo_startTime,"%Y%m%d%H") != #{refuseMoNo.dateHourForNoOneEmployee} ) as C on t_e_order.mo_no = C.mo_no) as B on SSS.e_no =B.e_no) left join t_member on t_member.m_no = B.m_no) as AA inner join (select t_employee.c_no, if((AVG(D.mo_star)*4/5)>=1,(AVG(D.mo_star)*4/5),1) as starGrade from ((select mo_no, mo_startTime,mo_star from t_m_order where (date_format(mo_startTime, "%Y%m") = if(substring((date_format(now(), "%Y%m")-1),5,2)='00', concat(substring((date_format(now(), "%Y%m")-1),1,4)-1,'12'), date_format(now(), "%Y%m")-1))) as D inner join t_e_order on D.mo_no = t_e_order.mo_no) inner join t_employee on t_employee.e_no = t_e_order.e_no group by t_employee.c_no) as BB on AA.c_no= BB.c_no) inner join (select t_employee.c_no, count(t_service.s_price), sum(t_service.s_price), (case when sum(t_service.s_price)-#{prevMonthOutCome}=0 then 3 when sum(t_service.s_price)-#{prevMonthOutCome} >2000000 then 1.1 when sum(t_service.s_price)-#{prevMonthOutCome} >1600000 then 1.3 when sum(t_service.s_price)-#{prevMonthOutCome} >1400000 then 1.5 when sum(t_service.s_price)-#{prevMonthOutCome} >1200000 then 1.7 when sum(t_service.s_price)-#{prevMonthOutCome} >1000000 then 1.9 when sum(t_service.s_price)-#{prevMonthOutCome} >500000 then 2.1 when sum(t_service.s_price)-#{prevMonthOutCome} >100000 then 2.3 when sum(t_service.s_price)-#{prevMonthOutCome} >50000 then 2.5 when sum(t_service.s_price)-#{prevMonthOutCome} >20000 then 2.7 when sum(t_service.s_price)-#{prevMonthOutCome} >10000 then 2.9 else 2.95 end) as profitGrade from (((t_m_order inner join t_service on t_m_order.s_no=t_service.s_no) inner join t_e_order on t_e_order.mo_no=t_m_order.mo_no) inner join t_employee on t_employee.e_no= t_e_order.e_no) where date_format(t_m_order.mo_startTime, "%Y%m") = if(substring((date_format(now(), "%Y%m")-1),5,2)='00', concat(substring((date_format(now(), "%Y%m")-1),1,4)-1,'12'), date_format(now(), "%Y%m")-1) group by t_employee.c_no) as ZZZ on AA.c_no = ZZZ.c_no) as LastOne left join ((select * from t_cancelCall where mo_no=#{mo_no}) as t_cancelCall) on t_cancelCall.e_no = LastOne.e_no where t_cancelCall.e_no is null order by totalGrade desc, starGrade desc limit 1) where mo_no=#{mo_no}; END

update t_member m, t_m_order mo, t_e_order eo 
	set m.m_name=#{m_name}, m.m_phone=#{m_phone}, m.m_lon=#{m_lon}, m.m_lat=#{m_lat}, m.m_gu=#{m_gu}, m.m_address=#{m_address},
		eo.e_no=#{e_no}, eo.eo_memo=#{eo_memo},
		mo.mo_startTime=#{mo_startTime}, mo.mo_endTime=#{mo_endTime}, mo.mo_svcName=#{mo_svcName}, mo.mo_cnt = #{mo_cnt}
	where mo.mo_no=#{mo_no}
		and mo.m_no = m.m_no
		and eo.mo_no = #{mo_no}

update t_m_order mo, t_e_order eo 
	set eo.e_no=#{e_no}, 
		mo.mo_cnt = #{mo_cnt}, mo.mo_startTime=#{mo_startTime}, mo.mo_endTime=#{mo_endTime}
	where mo.mo_no=#{mo_no}
		and eo.mo_no = mo.mo_no

 		update t_e_order set eo_status = #{eo_status} 
 							, eo_startTime = #{eo_startTime}  
 							, eo_endTime = #{eo_endTime} 
		where mo_no = #{mo_no}
